import React from 'react';
import './signup.css'

import TextField from '@material-ui/core/TextField';
import Checkbox from '@material-ui/core/Checkbox'
import Button from '@material-ui/core/Button'


class Signup extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            firstName: "",
            firstNameFlag: false,
            firstNameErrorMsg: "",

            lastName: "",
            lastNameFlag: false,
            lastNameErrorMsg: "",

            email: "",
            emailFlag: false,
            emailErrorMsg: "",
        }
    }
    clickShowPass = () => {
        this.setState({
            showPassword: !this.state.showPassword,
        });
    };


    change = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
        console.log(e.target.value);
    }

    validate = () => {
        let isError = false
        this.setState({ firstNameFlag: false })
        this.setState({ firstNameErrorMsg: "" })

        this.setState({ lastNameFlag: false })
        this.setState({ lastNameErrorMsg: "" })

        if (this.state.firstName.length === 0) {
            this.setState({ firstNameFlag: true })
            this.setState({ firstNameErrorMsg: "Enter first name" })
            isError = true
        }
        if (this.state.lastName.length === 0) {
            this.setState({ lastNameFlag: true })
            this.setState({ lastNameErrorMsg: "Enter last name" })
            isError = true
        }
        // if(this.state.lastName.length === 0 &&  this.state.firstName.length === 0 ){
        //     this.setState({ firstNameFlag: true })
        //     this.setState({ lastNameFlag: true })
        //     this.setState({ firstNameErrorMsg: "Enter firstname and lastname" })
        //     this.setState({ lastNameErrorMsg: "" })
        // }

        if (
            !/[a-zA-Z0-9._]+[@]{1}[a-zA-Z120-9]*[.]{1}[a-zA-Z]*$/.test(
                this.state.email
            )
        ) {
            this.setState({ emailFlag: true })
            this.setState({ emailErrorMsg: "Enter first name" })
            isError = true
        }
        return isError



    }




    onSubmit = (e) => {
        const err = this.validate();
        if (!err) {

            console.log("calling api");
        }
        else {
            console.log("reg failed")
        }
    }


    render() {

        return (
            <div className="registrationContainer">
                <div className="registrationContainerBox">
                    <div className="registrationContainerHeader">
                        <span className="registrationInlineTitle">
                            <b>
                                <font color="#1976d2">F</font>
                                <font color="#e53935">u</font>
                                <font color="#ffb74d">n</font>
                                <font color="#1976d2">d</font>
                                <font color="#388e3c">o</font>
                                <font color="#e53935">o</font>
                            </b>
                        </span>
                        <div className="registrationContainerHeaderText">Create your Fundoo Account </div>
                        <div className="formContainer">
                            <form className="form">

                                <div className="formInput">
                                    <div className="formInputField">
                                        <TextField
                                            name="firstName"
                                            onChange={(e) => this.change(e)}
                                            value={this.state.firstName}
                                            error={this.state.firstNameFlag}
                                            helperText={this.state.firstNameErrorMsg}
                                            label="First Name"
                                            size="small"
                                            variant="outlined"
                                            fullWidth />
                                    </div>
                                    <div className="formInputField">
                                        <TextField
                                            name="lastName"
                                            onChange={(e) => this.change(e)}
                                            value={this.state.lasttName}
                                            error={this.state.lastNameFlag}
                                            helperText={this.state.lastNameErrorMsg}
                                            label="Last Name"
                                            size="small"
                                            variant="outlined"
                                            fullWidth />
                                    </div>
                                </div>

                                <div className="formInput">
                                    <div className="formInputField">
                                        <TextField
                                            name="email"
                                            value={this.state.email}
                                            helperText={this.state.emailErrorMsg}
                                            error={this.state.emailFlag}
                                            onChange={(e) => this.change(e)}
                                            label="Email"
                                            size="small"
                                            variant="outlined"
                                            fullWidth />
                                    </div>
                                </div>


                                <div className="formInput">
                                    <div className="formInputField">
                                        <TextField
                                            id="password"
                                            label="Password"
                                            size="small"
                                            variant="outlined"
                                            type={this.state.showPassword ? "text" : "password"}
                                            fullWidth />
                                    </div>
                                    <div className="formInputField">
                                        <TextField
                                            label="confirm password"
                                            size="small"
                                            variant="outlined"
                                            type={this.state.showPassword ? "text" : "password"}
                                            fullWidth />
                                    </div>
                                </div>

                                <span className="checkBoxInputs">
                                    <Checkbox
                                        color="primary"
                                        className="showPass"
                                        onClick={this.clickShowPass}
                                    />
                                    Show Password
                                </span>


                                <div className="footerButtons">
                                    <div className="signInLink">
                                        <Button
                                            color="primary">
                                            <b>Sign in insted</b>
                                        </Button>
                                    </div>
                                    <div className="nextButton">
                                        <Button
                                            variant="contained"
                                            color="primary"
                                            onClick={this.onSubmit}
                                        >
                                            Submit
                                        </Button>
                                    </div>
                                </div>


                            </form>
                            <div className="regImg">
                                <img src="https://ssl.gstatic.com/accounts/signup/glif/account.svg" alt="google imge" />
                                <p className="imgText">
                                    One account. All of Fundoo working for you.
                                 </p>
                            </div>
                        </div>
                    </div>
                </div>

            </div>
        )

    }
}


export default Signup;
